con:config.script:
import com.eviware.soapui.support.XmlHolder
import com.eviware.soapui.impl.wsdl.testcase.WsdlTestRunContext

//***log.info TESTING***//
//log.info "Hello"
//
//log.info context.expand('${#TestCase#name}')
//log.info context.expand('${#TestCase#age}')
// testRunner.testCase.testSuite.testCases["GetEmp"].setPropertyValue("id", "456")
//log.info testRunner.testCase.testSuite.testCases["GetEmp"].getPropertyValue("id")
//log.info testRunner.testCase.testSuite.testCases["AddEmp"].getPropertyValue("age")
//
//log.info testRunner.testCase.testSuite.project.getPropertyValue("gender")
//
//log.info testRunner.testCase.getPropertyValue("dept")
//***log.info TESTING***//

//hit addEmp
def addReq = testRunner.testCase.testSuite.testCases["AddEmp"].testSteps["add"].getPropertyValue("Request")
def xmlAddReq = new XmlHolder(addReq)
def name = testRunner.testCase.testSuite.testCases["AddEmp"].getPropertyValue("name")
def id = testRunner.testCase.testSuite.testCases["AddEmp"].getPropertyValue("id")
def age = testRunner.testCase.testSuite.testCases["AddEmp"].getPropertyValue("age")
xmlAddReq.setNodeValue("//typ:addEmployee/typ:name", name)
xmlAddReq.setNodeValue("//typ:addEmployee/typ:id", id)
xmlAddReq.setNodeValue("//typ:addEmployee/typ:age", age)
xmlAddReq.setNodeValue("//typ:addEmployee/typ:Department", "IT")
def newXMLAddReq = xmlAddReq.getXml()

testRunner.testCase.testSuite.testCases["AddEmp"].testSteps["add"].setPropertyValue("Request", newXMLAddReq)

def addTestStep = testRunner.testCase.testSuite.testCases["AddEmp"].testSteps["add"]
def addEmpContext = new WsdlTestRunContext(addTestStep)

addTestStep.run(testRunner, addEmpContext)

//hit getEmp
def getReq = testRunner.testCase.testSuite.testCases["GetEmp"].testSteps["get"].getPropertyValue("Request")
def xmlGetReq = new XmlHolder(getReq)  
xmlGetReq.setNodeValue("//typ:getEmployeeDetails/typ:employeeName", testRunner.testCase.testSuite.testCases["AddEmp"].getPropertyValue("name"))
def newGetXml = xmlGetReq.getXml()

testRunner.testCase.testSuite.testCases["GetEmp"].testSteps["get"].setPropertyValue("Request", newGetXml)

def getTestStep = testRunner.testCase.testSuite.testCases["GetEmp"].testSteps["get"]
def getEmpContext = new WsdlTestRunContext(getTestStep)

getTestStep.run(testRunner, getEmpContext)

//validation
def getRes = testRunner.testCase.testSuite.testCases["GetEmp"].testSteps["get"].getPropertyValue("Response")

def getEmpRes = new XmlHolder(getRes)
def getResponseName =  getEmpRes.getNodeValue("//ns:getEmployeeDetailsResponse/ns:return/ns:name")

assert getResponseName==name

//delete
def delReq = testRunner.testCase.testSuite.testCases["DeleEmp"].testSteps["del"].getPropertyValue("Request")
def delReqXml = new XmlHolder(delReq)
delReqXml.setNodeValue("//typ:employeeName", name)
def newDelXml = delReqXml.getXml()
testRunner.testCase.testSuite.testCases["DeleEmp"].testSteps["del"].setPropertyValue("Request", newDelXml)

def delTestStep = testRunner.testCase.testSuite.testCases["DeleEmp"].testSteps["del"]
def delWsdlContext = new WsdlTestRunContext(delTestStep)
delTestStep.run(testRunner, delWsdlContext)
log.info "ok"
